import React from "react";

import "../styles.css";

import Notes from "./Notes";

class ReactForm extends React.Component {
    static notes = new Notes();

    constructor() {
        super();
        this.state = {
            titleNote: "",
            contentNote: ""
        };
        this.handleChange = this.handleChange.bind(this);
        this.createNote = this.createNote.bind(this);
    }

    handleChange(event) {
        let nameTarget = event.target.name;
        if (nameTarget === "title") {
            console.log("ok");
            this.setState({titleNote: event.target.value});
        } else if (nameTarget === "content") {
            this.setState({contentNote: event.target.value});
        }
    }

    createNote() {
        this.notes.setNote({title: "ok", content: "aie"});
    }

    render() {
        return (
            <div id="form-note" className="container">
                <input onChange={this.handleChange} className="form-control" placeholder="Title" name="title"/>
                <input onChange={this.handleChange} className="form-control" placeholder="Take a note..." name="content" />
                <button onClick={this.createNote} className="btn">Add</button>
            </div>
        )
    }
}

export default ReactForm;
