import React from "react";

import "../styles.css";

import notes from "../notes";

class ReactForm extends React.Component {
    constructor() {
        super();
        this.state = {
            titleNote: "",
            contentNote: ""
        };
        this.getInput = this.getInput.bind(this);
    }

    getInput(event) {
        let nameTarget = event.name;
        if (nameTarget === "title") {
            this.setState({titleNote: event.target.value});
        } else if (nameTarget === "content") {
            this.setState({contentNote: event.target.value});
        }
    }

    render() {
        return (
            <div id="form-note" className="container">
                <input onChange={this.getInput} className="form-control" placeholder="Title" name="title"/>
                <input onChange={this.getInput} className="form-control" placeholder="Take a note..." name="content" />
                <button className="btn">Add</button>
                <p>{this.state.titleNote} {this.state.contentNote}</p>
            </div>
        )
    }
}

export default ReactForm;
